package jan19;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String  day ="Wensday";
	
	switch (day)
		{
	case "sunday" : System.out.println("day 1 of  week ");
	break;
	
	case "monday" : System.out.println("day 2 of  week ");
	break;
	
	case "tuesday" : System.out.println("day 3 of  week ");
	break;
	
	case "Wensday" : System.out.println("day 4 of  week ");
	break;
	
	case "thrusday" : System.out.println("day 5 of  week ");
	break;
	
	case "friday" : System.out.println("day 6 of  week ");
	break;
	
	case "saturday" : System.out.println("day 7 of  week ");
	break;
	
	case "defaul" : System.out.println("please enter correct input ");
	break;
	}
	}
	

}
