		{
			System.out.println("subtraction is" + (x-y));
		}
		public void mul(int x, int y)
		{
			System.out.println("multiplication is" + (x*y));
		}
		public void div(int x, int y)
		{
			System.out.println("division is" + (x/y));
		}
